type EmailOptionTypes = {
  title: string;
  subTitle: string;
  buttonText: string;
  linkToGo?: string;
  action?: () => void;
  resendText: string;
  resendAction: () => void;
  loading?: boolean;
  actionLoading?: boolean;
};

const VerifySubscriberEmailComponent = ({
  title,
  subTitle,
  buttonText,
  linkToGo,
  action,
  resendText,
  resendAction,
  loading,
  actionLoading,
}: EmailOptionTypes) => {
  return (
    <div
      className={
        "w-full h-screen flex items-center justify-center bg-background"
      }
    >
      <div
        className={
          "w-11/12 max-w-lg max-h-[91.67%] p-8 flex flex-col items-center justify-center gap-6 bg-white rounded-md overflow-hidden"
        }
      >
        <EmailIllustrationComponent />

        <div className={"space-y-2"}>
          <h1 className={"text-2xl text-center font-grotesk font-bold"}>
            {title}
          </h1>
          <p className={"text-gray-dark text-center"}>{subTitle}</p>
        </div>

        {linkToGo ? (
          <Link href={linkToGo}>
            <ButtonComponent size={"base"} variant={"filled"}>
              {buttonText}
            </ButtonComponent>
          </Link>
        ) : (
          <ButtonComponent size={"base"} onClick={action} variant={"filled"}>
            {actionLoading ? "Wait..." : buttonText}
          </ButtonComponent>
        )}

        <p>
          {resendText}{" "}
          <button
            onClick={() => resendAction()}
            className={"text-primary font-medium"}
          >
            {loading ? "Resending..." : "Resend"}
          </button>
        </p>
      </div>
    </div>
  );
};

export default VerifySubscriberEmailComponent;
